package basics;

public class Comparisionoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
		int y=10;
		
		System.out.println("equal to operator x=5 and y=10:");
		System.out.println(x==y);
		
		System.out.println("not equal to operator x=5 and y=10:");
		System.out.println(x!=y);
		
		System.out.println("greater than operator x=5 and y=10:");
		System.out.println(x>y);
		
		System.out.println("less than operator x=5 and y=10:");
		System.out.println(x<y);
		
		System.out.println("Greater than or equal to operator x=5 and y=10:");
		System.out.println(x>=y);
		
		System.out.println("less than or equal to operator x=5 and y=10:");
		System.out.println(x<=y);
		
		

	}

}
