package basics;

public class Javawhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//while
		int i=0;
		while(i<5)
		{
			System.out.println(i);
			i++;
		}
		//do while
		int i1=0;
		do {
			System.out.println(i1);
			i1=i1+1;
		}while(i1<5);
		

	}

}
