package oops;

public class Person {
	String fname="Jhon";
	String lname="Doe";
	int age=22;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Person myobj=new Person();
		System.out.println("Name: "+myobj.fname+" "+myobj.lname);
		System.out.println("Age: "+myobj.age);

	}

}
